import React, { useState } from 'react';

export const AuthContext = React.createContext({});

export const AuthProvider = async (props) => {
    const [user, setUser] = useState([]);

    const inc = ['gender', 'name', 'dob', 'nat', 'id', 'location', 'fone', 'email', 'picture'];
    const arr = inc.join(',');
    const data = await fetch(`https://randomuser.me/api/?results=50&inc=${arr}&pages=1`)
        .then(response => response.json())
        .then(data => data.results)
        .catch(error => console.log(error));


    return (
        <AuthContext.Provider value={{user, setUser, data} }>
            {props.children}
        </AuthContext.Provider>
    )
}
