import  React from 'react';
import { AuthContext} from '../../provider/data';

import './styles.css'
function TextInput({search, handleChange }) {
    const { users }  = React.useContext(AuthContext);

    const filteredUsers = search
    ? users.filter((user) => {
      return user.name.first.toLowerCase().includes(search.toLowerCase())
    })
    : users;

    return (
        <div className="container d-flex input-group mb-4">
            <input
                className="form-control"
                onChange={handleChange}
                value={search}
                type="search"
                placeholder="Pesquise aqui"
            />
        </div>
    );
};
export { TextInput }
