import { TextInput } from '../../components/Input';
import { Table } from '../../components/Table';

// - hooks e services
import { useSearch } from '../../hooks/useSearch';

import './styles.css';

function App() {
  const { users, value }  = React.useContext(AuthContext);
  console.log(value);
