import React, { useState } from 'react'
import { AuthContext } from '../provider/data';

export const useSearch = () => {
    const { users } = React.createContext(AuthContext);
    const [search, setSearch] = useState('');

    const handleChange = (e) => {
        const { value } = e.target;
        setSearch(value);
    };

    function buscar(valor){
      if(valor){
        users.filter((user) => {
          return user.name.first.toLowerCase().includes(valor.toLowerCase())
        });
      }else {
        return users;
      }
    }

    return { search, handleChange, buscar };
}
